   CREATE TASK COMMERCE_BRONZE.COMMERCE_SOURCE.LOAD_CUSTOMERS_INCREMENTAL  USER_TASK_MANAGED_INITIAL_WAREHOUSE_SIZE = MEDIUM  SCHEDULE = 'USING CRON 0 8 * * * America/Los_Angeles'  COMMENT = 'Task to load incremental customer data'  AS  INSERT INTO COMMERCE_BRONZE.COMMERCE_INCREMENTAL.CUSTOMERS SELECT * FROM COMMERCE_BRONZE.COMMERCE_SOURCE.CUSTOMERS_STREAM WHERE METADATA$ACTION = 'INSERT' 