   CREATE TASK commerce_db_demo.commerce_schema.load_categories_task  WAREHOUSE = COMPUTE_WH  SCHEDULE = 'USING CRON 0 8 * * * America/Los_Angeles'  COMMENT = 'Task to load incremental category data daily at 8 AM'  AS  INSERT INTO commerce_db_demo.commerce_schema_incr.categories SELECT * FROM commerce_db_demo.commerce_schema.categories WHERE category_id NOT IN (SELECT category_id FROM commerce_db_demo.commerce_schema_incr.categories); 