   CREATE TASK commerce_db_demo.commerce_schema.load_customers_task  WAREHOUSE = COMPUTE_WH  SCHEDULE = 'USING CRON 0 8 * * * America/Los_Angeles'  COMMENT = 'Task to load incremental customer data daily at 8 AM'  AS  INSERT INTO commerce_db_demo.commerce_schema_incr.customers SELECT * FROM commerce_db_demo.commerce_schema.customers WHERE customer_id NOT IN (SELECT customer_id FROM commerce_db_demo.commerce_schema_incr.customers); 