   CREATE TASK commerce_db_demo.commerce_schema.load_products_task  WAREHOUSE = COMPUTE_WH  SCHEDULE = 'USING CRON 0 8 * * * America/Los_Angeles'  COMMENT = 'Task to load incremental product data daily at 8 AM'  AS  INSERT INTO commerce_db_demo.commerce_schema_incr.products SELECT * FROM commerce_db_demo.commerce_schema.products WHERE product_id NOT IN (SELECT product_id FROM commerce_db_demo.commerce_schema_incr.products); 