   CREATE TASK COMMERCE_DEV.COMMERCE_DATA.TASK_LOAD_CUSTOMERS_INCREMENTAL  WAREHOUSE = COMPUTE_WH  SCHEDULE = 'USING CRON 0 8 * * * America/New_York'  COMMENT = 'Task to load incremental customer data daily'  AS  MERGE INTO COMMERCE_DEV.COMMERCE_INCREMENTAL.CUSTOMERS t USING (SELECT * FROM COMMERCE_DEV.COMMERCE_DATA.CUSTOMERS s WHERE NOT EXISTS (SELECT 1 FROM COMMERCE_DEV.COMMERCE_INCREMENTAL.CUSTOMERS t WHERE t.customer_id = s.customer_id)) s ON t.customer_id = s.customer_id WHEN NOT MATCHED THEN INSERT (customer_id, first_name, last_name, email, phone, date_joined) VALUES (s.customer_id, s.first_name, s.last_name, s.email, s.phone, s.date_joined) 