   CREATE TASK COMMERCE_DEV.COMMERCE_DATA.TASK_LOAD_PRODUCTS_INCREMENTAL  WAREHOUSE = COMPUTE_WH  SCHEDULE = 'USING CRON 0 8 * * * America/New_York'  COMMENT = 'Task to load incremental product data daily'  AS  MERGE INTO COMMERCE_DEV.COMMERCE_INCREMENTAL.PRODUCTS t USING (SELECT * FROM COMMERCE_DEV.COMMERCE_DATA.PRODUCTS s WHERE NOT EXISTS (SELECT 1 FROM COMMERCE_DEV.COMMERCE_INCREMENTAL.PRODUCTS t WHERE t.product_id = s.product_id)) s ON t.product_id = s.product_id WHEN NOT MATCHED THEN INSERT (product_id, product_name, category_id, unit_price, stock_quantity) VALUES (s.product_id, s.product_name, s.category_id, s.unit_price, s.stock_quantity) 