   CREATE TASK COMMERCE_DEV.COMMERCE_INCR.LOAD_CATEGORIES_INCR  WAREHOUSE = ANALYTICS_WH  SCHEDULE = 'USING CRON 0 8 * * * America/New_York'  COMMENT = 'Daily incremental load for Categories table'  AS  MERGE INTO COMMERCE_DEV.COMMERCE_INCR.CATEGORIES t USING (SELECT * FROM COMMERCE_DEV.COMMERCE_SRC.CATEGORIES) s ON t.category_id = s.category_id WHEN MATCHED THEN UPDATE SET t.category_name = s.category_name, t.description = s.description WHEN NOT MATCHED THEN INSERT (category_id, category_name, description) VALUES (s.category_id, s.category_name, s.description) 