   CREATE TASK COMMERCE_DEV.COMMERCE_INCR.LOAD_CUSTOMERS_INCR  WAREHOUSE = ANALYTICS_WH  SCHEDULE = 'USING CRON 0 8 * * * America/New_York'  COMMENT = 'Daily incremental load for Customers table'  AS  MERGE INTO COMMERCE_DEV.COMMERCE_INCR.CUSTOMERS t USING (SELECT * FROM COMMERCE_DEV.COMMERCE_SRC.CUSTOMERS) s ON t.customer_id = s.customer_id WHEN MATCHED THEN UPDATE SET t.first_name = s.first_name, t.last_name = s.last_name, t.email = s.email, t.phone = s.phone, t.date_joined = s.date_joined WHEN NOT MATCHED THEN INSERT (customer_id, first_name, last_name, email, phone, date_joined) VALUES (s.customer_id, s.first_name, s.last_name, s.email, s.phone, s.date_joined) 