   CREATE TASK COMMERCE_DEV.COMMERCE_INCR.LOAD_PRODUCTS_INCR  WAREHOUSE = ANALYTICS_WH  SCHEDULE = 'USING CRON 0 8 * * * America/New_York'  COMMENT = 'Daily incremental load for Products table'  AS  MERGE INTO COMMERCE_DEV.COMMERCE_INCR.PRODUCTS t USING (SELECT * FROM COMMERCE_DEV.COMMERCE_SRC.PRODUCTS) s ON t.product_id = s.product_id WHEN MATCHED THEN UPDATE SET t.product_name = s.product_name, t.category_id = s.category_id, t.unit_price = s.unit_price, t.stock_quantity = s.stock_quantity WHEN NOT MATCHED THEN INSERT (product_id, product_name, category_id, unit_price, stock_quantity) VALUES (s.product_id, s.product_name, s.category_id, s.unit_price, s.stock_quantity) 