   CREATE TASK COMMERCE_DEV.COMMERCE_SOURCE.LOAD_CATEGORIES_INCREMENTAL  WAREHOUSE = COMPUTE_WH  SCHEDULE = 'USING CRON 0 8 * * * America/New_York'  COMMENT = 'Task to load incremental categories data'  AS  MERGE INTO COMMERCE_DEV.COMMERCE_INCREMENTAL.CATEGORIES t USING COMMERCE_DEV.COMMERCE_SOURCE.CATEGORIES s ON t.category_id = s.category_id WHEN NOT MATCHED THEN INSERT (category_id, category_name, description) VALUES (s.category_id, s.category_name, s.description) 