   CREATE TASK COMMERCE_DEV.COMMERCE_SOURCE.LOAD_CUSTOMERS_INCREMENTAL  WAREHOUSE = COMPUTE_WH  SCHEDULE = 'USING CRON 0 8 * * * America/New_York'  COMMENT = 'Task to load incremental customer data'  AS  MERGE INTO COMMERCE_DEV.COMMERCE_INCREMENTAL.CUSTOMERS t USING COMMERCE_DEV.COMMERCE_SOURCE.CUSTOMERS s ON t.customer_id = s.customer_id WHEN NOT MATCHED THEN INSERT (customer_id, first_name, last_name, email, phone, date_joined) VALUES (s.customer_id, s.first_name, s.last_name, s.email, s.phone, s.date_joined) 