   CREATE TASK COMMERCE_DEV.COMMERCE_SOURCE.LOAD_PRODUCTS_INCREMENTAL  WAREHOUSE = COMPUTE_WH  SCHEDULE = 'USING CRON 0 8 * * * America/New_York'  COMMENT = 'Task to load incremental product data'  AS  MERGE INTO COMMERCE_DEV.COMMERCE_INCREMENTAL.PRODUCTS t USING COMMERCE_DEV.COMMERCE_SOURCE.PRODUCTS s ON t.product_id = s.product_id WHEN NOT MATCHED THEN INSERT (product_id, product_name, category_id, unit_price, stock_quantity) VALUES (s.product_id, s.product_name, s.category_id, s.unit_price, s.stock_quantity) 