   CREATE TASK DEV_COMM.COMMERCE.LOAD_INCREMENTAL_CUSTOMERS  WAREHOUSE = COMPUTE_WH  SCHEDULE = 'USING CRON 0 8 * * * America/New_York'  COMMENT = 'Task to load incremental customer data'  AS  INSERT INTO DEV_COMM.COMMERCE_INCREMENTAL.CUSTOMERS SELECT * FROM DEV_COMM.COMMERCE.CUSTOMERS WHERE date_joined >= CURRENT_DATE() 