   CREATE TASK FINANCE_DEV.CORE.TRANSACTION_DAILY_LOAD  WAREHOUSE = COMPUTE_WH  SCHEDULE = 'USING CRON 0 8 * * * America/Los_Angeles'  COMMENT = 'Daily task to load incremental data from transaction table with deduplication'  AS  INSERT OVERWRITE INTO finance_dev.clean.transaction SELECT DISTINCT transaction_id, * FROM (SELECT *, ROW_NUMBER() OVER (PARTITION BY transaction_id ORDER BY METADATA$ACTION_TSTAMP DESC) as rn FROM finance_dev.core.transaction_stream WHERE METADATA$ACTION IN ('INSERT', 'UPDATE')) WHERE rn = 1; 