   CREATE TASK RETAIL_DB.SOURCE_SCHEMA.LOAD_CATEGORIES_TASK  WAREHOUSE = COMPUTE_WH  SCHEDULE = 'USING CRON 0 8 * * * America/Los_Angeles'  COMMENT = 'Task to load incremental category data daily at 8 AM'  AS  MERGE INTO RETAIL_DB.INCREMENTAL_SCHEMA.CATEGORIES tgt USING (SELECT * FROM RETAIL_DB.SOURCE_SCHEMA.CATEGORIES src WHERE src.last_modified > (SELECT COALESCE(MAX(last_modified), '1900-01-01'::timestamp) FROM RETAIL_DB.INCREMENTAL_SCHEMA.CATEGORIES)) src ON tgt.category_id = src.category_id WHEN MATCHED THEN UPDATE SET tgt.category_name = src.category_name, tgt.description = src.description, tgt.last_modified = src.last_modified WHEN NOT MATCHED THEN INSERT (category_id, category_name, description, last_modified) VALUES (src.category_id, src.category_name, src.description, src.last_modified); 