  CREATE TASK commerce_bronze.commerce_raw.load_categories_incremental  WAREHOUSE = COMPUTE_WH  COMMENT = 'Task to load incremental category data'  AS  MERGE INTO commerce_bronze.commerce_incremental.categories t USING commerce_bronze.commerce_raw.categories_stream s ON t.category_id = s.category_id WHEN MATCHED THEN UPDATE SET t.category_name = s.category_name, t.description = s.description WHEN NOT MATCHED THEN INSERT (category_id, category_name, description) VALUES (s.category_id, s.category_name, s.description) 