   CREATE TASK commerce_bronze.commerce_raw.load_customers_incremental  WAREHOUSE = COMPUTE_WH  SCHEDULE = 'USING CRON 0 8 * * * America/Los_Angeles'  COMMENT = 'Task to load incremental customer data'  AS  MERGE INTO commerce_bronze.commerce_incremental.customers t USING commerce_bronze.commerce_raw.customers_stream s ON t.customer_id = s.customer_id WHEN MATCHED THEN UPDATE SET t.first_name = s.first_name, t.last_name = s.last_name, t.email = s.email, t.phone = s.phone, t.date_joined = s.date_joined WHEN NOT MATCHED THEN INSERT (customer_id, first_name, last_name, email, phone, date_joined) VALUES (s.customer_id, s.first_name, s.last_name, s.email, s.phone, s.date_joined) 