  CREATE TASK commerce_bronze.commerce_raw.load_order_items_incremental  WAREHOUSE = COMPUTE_WH  COMMENT = 'Task to load incremental order items data'  AS  MERGE INTO commerce_bronze.commerce_incremental.order_items t USING commerce_bronze.commerce_raw.order_items_stream s ON t.order_item_id = s.order_item_id WHEN MATCHED THEN UPDATE SET t.order_id = s.order_id, t.product_id = s.product_id, t.quantity = s.quantity, t.unit_price = s.unit_price, t.subtotal = s.subtotal WHEN NOT MATCHED THEN INSERT (order_item_id, order_id, product_id, quantity, unit_price, subtotal) VALUES (s.order_item_id, s.order_id, s.product_id, s.quantity, s.unit_price, s.subtotal) 