  CREATE TASK commerce_bronze.commerce_raw.load_orders_incremental  WAREHOUSE = COMPUTE_WH  COMMENT = 'Task to load incremental order data'  AS  MERGE INTO commerce_bronze.commerce_incremental.orders t USING commerce_bronze.commerce_raw.orders_stream s ON t.order_id = s.order_id WHEN MATCHED THEN UPDATE SET t.customer_id = s.customer_id, t.order_date = s.order_date, t.status = s.status, t.total_amount = s.total_amount WHEN NOT MATCHED THEN INSERT (order_id, customer_id, order_date, status, total_amount) VALUES (s.order_id, s.customer_id, s.order_date, s.status, s.total_amount) 