  CREATE TASK commerce_bronze.commerce_raw.load_products_incremental  WAREHOUSE = COMPUTE_WH  COMMENT = 'Task to load incremental product data'  AS  MERGE INTO commerce_bronze.commerce_incremental.products t USING commerce_bronze.commerce_raw.products_stream s ON t.product_id = s.product_id WHEN MATCHED THEN UPDATE SET t.product_name = s.product_name, t.category_id = s.category_id, t.unit_price = s.unit_price, t.stock_quantity = s.stock_quantity WHEN NOT MATCHED THEN INSERT (product_id, product_name, category_id, unit_price, stock_quantity) VALUES (s.product_id, s.product_name, s.category_id, s.unit_price, s.stock_quantity) 